/**
 * Parses the markdown knowledge base to extract a list of unique suggestions.
 * @param markdown The markdown string containing the knowledge base.
 * @returns An array of unique suggestion strings.
 */
export const parseKnowledgeBaseForSuggestions = (markdown: string): string[] => {
  const suggestionsFromMarkdown = new Set<string>();
  const lines = markdown.split('\n');

  for (const line of lines) {
    // Check if it's a table data row and has at least 3 '|' characters
    if (line.trim().startsWith('|') && !line.includes(':---')) {
      const columns = line.split('|').map(c => c.trim());
      // column[0] is empty, column[1] is Ngach Chinh, column[2] is Keywords
      if (columns.length > 2) {
        const keywordsColumn = columns[2]; // "Chủ đề/Keywords cụ thể"
        const keywords = keywordsColumn.split(',').map(k => k.trim());
        
        for (const keyword of keywords) {
          if (keyword) {
            // Clean up the keyword: remove parentheses and their content, then trim again.
            const cleanedKeyword = keyword.replace(/\([^)]*\)/g, '').trim();
            // Ensure it's not an empty string after cleaning
            if (cleanedKeyword) {
              suggestionsFromMarkdown.add(cleanedKeyword);
            }
          }
        }
      }
    }
  }

  // Combine suggestions from markdown and the extended list
  const combinedSuggestions = new Set([...suggestionsFromMarkdown, ...extendedNicheSuggestions]);

  return Array.from(combinedSuggestions);
};